import React, { Component, useEffect, useState, handleChange } from "react";
import Banner from "./Banner";
import logo from "./logo.svg";
import search_icon from "./search-icon.png";

class Artist extends Component {
  constructor(props) {
    super(props);
    this.state = {
      artist: 'bab'
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    let currentSearch = '';
    let newSearch = '';

    if (e.target.value !== '') {
      currentSearch = this.props.artist;
      newSearch = currentSearch
    } else {
      newSearch = this.props.artist;
    }
    this.setState({artist: newSearch})
  }

  render() {
    return (
      <div >
        <span className="search-span">
                  <input type="search"
                         className="search-box"
                         id="artist-search"
                         src={"search_icon"}
                         placeholder="Search Artist"
                         autoComplete="off"
                         spellCheck="false"
                         onChange={this.handleChange}
                  />
        </span>
        <p>search:  {this.state.artist}</p>
      </div>
    )
  }
}

class Analyze2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      artist: ''
    }
  }

  render() {
    return (
      <div id="analyze" className="page-body">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <Artist artist={this.state.artist} />
      </div>
    )
  }
}

