      </div>

      )
  } else {
    return(
      <p>No artist entered.</p>
    )
  }
}


function Analyze() {
  const [currentSearch, setSearch] = useState('');
  const [currentResult, setResult] = useState(null);

  // WORKS!!!
  const fetchArtist = () => {
    fetch('/api/analyze?artist='+currentSearch)
      .then(res => res.json())
