import React, { Component, useEffect, useState, handleChange } from "react";
import Banner from "./Banner";
import logo from "./logo.svg";
import search_icon from "./search-icon.png";


function Analyze() {
  const [currentSearch, setSearch] = useState('');
  const [currentResult, setResult] = useState({});

  const fetchArtist = () => {
    fetch('/api/analyze?artist='+currentSearch).then(res => res.json()).then(data => {
      setResult({name: data.result.name, followers: data.result.followers});
    })
  }

  useEffect(() => {
    fetchArtist()
  }, []);

  // useEffect(() => {
  //   fetch('/api/analyze').then(res => res.json()).then(data => {
  //     setResult(data.result);
  //   });
  // }, []);

  function handleChange(e) {
    // let currentSearch = '';
    // let newSearch = '';
    setSearch(e.target.value)

    if (e.target.value !== '') {
      fetchArtist()
    }
  }

  return (
    <div id="analyze" className="page-body">
      <span className="search-span">
        <input type="search"
               className="search-box"
               id="artist-search"
               src={"search_icon"}
               placeholder="Search Artist"
               autoComplete="off"
               spellCheck="false"
               onChange={handleChange}
        />
      </span>
      <img src={logo} className="App-logo" alt="logo" />
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
      currentResult != {} ? <p>{currentResult.name}</p> : <p>"No arist entered."</p>
      <p>{currentResult.followers}</p>
    </div>
  );
};


