import React, { Component, useEffect, useState, handleChange } from "react";
import Banner from "./Banner";
import logo from "./logo.svg";
import search_icon from "./search-icon.png";

class Artist extends Component {
  render() {
    return (
      <div>
        ...
      </div>
    )
  }
}

export class Analyze2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      artist: ''
    }
  }



  render() {
    return (
      <div id="analyze" className="page-body">
        <span className="search-span">
          <input type="search" className="search-box" id="artist-search" src={"search_icon"} placeholder="Search Artist" autoComplete="off" spellCheck="false" />
        </span>
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <p>{this.state.artist}</p>
      </div>
    )
  }
}

function Analyze() {
  const [currentResult, setResult] = useState('');
  const fetchArtist = () => {
    const artistData = fetch('/api/analyze?artist='+currentResult).then(res => res.json()).then(data => {
      setResult(data.result);
    })
  }

  useEffect(() => {
    fetchArtist()
  }, []);

  // useEffect(() => {
  //   fetch('/api/analyze').then(res => res.json()).then(data => {
  //     setResult(data.result);
  //   });
  // }, []);

  function handleChange(e) {
    let currentSearch = '';
    let newSearch = '';

    if (e.target.value !== '') {
      currentSearch = this.props
      fetchArtist()
    }
  }

  return (
    <div id="analyze" className="page-body">
      <span className="search-span">
        <input type="search"
               className="search-box"
               id="artist-search"
               src={"search_icon"}
               placeholder="Search Artist"
               autoComplete="off"
               spellCheck="false"
               onChange={handleChange}
        />
      </span>
      <img src={logo} className="App-logo" alt="logo" />
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
      <p>{currentResult}</p>
    </div>
  );
};

export default Analyze;
