import React, { Component, useEffect, useState, handleChange } from "react";
import Banner from "./Banner";
import logo from "./logo.svg";
import search_icon from "./search-icon.png";

function fetchArtist(searchTerm) {
  return fetch('/api/analyze?artist=' + searchTerm)
    .then(res => {
      return res.json()
      // return Promise.resolve(res)
    })
    //.catch(error => {return Promise.reject(error)})
}

class Artist extends Component {
  constructor(props) {
    super(props);
    this.state = {
      artist: 'bab'
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    let searchTerm = '';
    let searchResults = '';

    if (e.target.value !== '') {
      searchTerm = e.target.value;
      //searchResults = e.target.value;
     searchResults = fetchArtist(searchTerm).then(response => {
        console.log(response);
        //return Object.assign({}, response.data)
       return response
      })
    } else {
      searchResults = this.props.artist.name;
    }
    // searchResults.then(data => {
    //   console.log('Request successful: ', data);
    //   }, err => {
    //   console.log('Request failed: ', err);
    // });
    console.log("search: " + searchResults)
    this.setState({artist: searchResults.name})
  }

  render() {
    return (
      <div >
        <span className="search-span">
                  <input type="search"
                         className="search-box"
                         id="artist-search"
                         src={"search_icon"}
                         placeholder="Search Artist"
                         autoComplete="off"
                         spellCheck="false"
                         onChange={this.handleChange}
                  />
        </span>
        <p>search:  {this.state.artist}</p>
      </div>
    )
  }
}

class Analyze2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      artist: ''
    }
  }

  render() {
    return (
      <div id="analyze" className="page-body">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <Artist artist={this.state.artist} />
      </div>
    )
  }
}

function Analyze() {
  const [currentResult, setResult] = useState('');
  const fetchArtist = () => {
    const artistData = fetch('/api/analyze?artist='+currentResult).then(res => res.json()).then(data => {
      setResult(data.result);
    })
  }

  useEffect(() => {
    fetchArtist()
  }, []);

  // useEffect(() => {
  //   fetch('/api/analyze').then(res => res.json()).then(data => {
  //     setResult(data.result);
  //   });
  // }, []);

  function handleChange(e) {
    let currentSearch = '';
    let newSearch = '';

    if (e.target.value !== '') {
      currentSearch = this.props.artist;
      fetchArtist()
    }
  }

  return (
    <div id="analyze" className="page-body">
      <span className="search-span">
        <input type="search"
               className="search-box"
               id="artist-search"
               src={"search_icon"}
               placeholder="Search Artist"
               autoComplete="off"
               spellCheck="false"
               onChange={handleChange}
        />
      </span>
      <img src={logo} className="App-logo" alt="logo" />
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
      <p>{currentResult}</p>
    </div>
  );
};

//export default Analyze;
export default Analyze2;
