      {/*  className="App-link"*/}
      {/*  href="https://reactjs.org"*/}
      {/*  target="_blank"*/}
      {/*  rel="noopener noreferrer"*/}
      {/*>*/}
      {/*  Learn React*/}
      {/*</a>*/}
      {/*<p>The current time is {currentTime}.</p>*/}
    </div>
  );
};

export default Home;
