      {/*<div id="result"></div>*/}



      <div id="login">
        <h1>Authorize Spotify Account</h1>
        <a href="/login" className="btn btn-login">Log in with Spotify</a>
      </div>
      <div id="loggedin">
        <div id="user-profile"/>
        <div id="oauth"/>
        <button className="btn btn-default" id="obtain-new-token">Obtain new token using the refresh token</button>
      </div>
    </div>
  );
};


export default Home;
