import React, { Component, useEffect, useState, handleChange } from "react";
import Banner from "./Banner";
import logo from "./logo.svg";
import search_icon from "./search-icon.png";


function Artist(props) {
  const currentResult = props.currentResult

  if (currentResult?.name) {
    return (
      <div>
        <p>Artist Name: {currentResult.name}</p>
        <p>Followers: {currentResult.followers}</p>
        <p>Genres: {currentResult.genres}</p>
      </div>

      )
  } else {
    return(
      <p>No artist entered.</p>
    )
  }
}


function Analyze() {
  const [currentSearch, setSearch] = useState('');
  const [currentResult, setResult] = useState(null);

  const fetchArtist = () => {
    fetch('/api/analyze?artist='+currentSearch).then(res => res.json()).then(data => {
      setResult({name: data.result.name, followers: data.result.followers});
    })
  }

  useEffect(() => {
    fetchArtist()
  }, []);



  // function handleChange(e) {
  //   const search = e.target.value
  //   setSearch({search}, () => {if (e.target.value !== '') {fetchArtist()}})
  // }

  function handleChange(e) {
    const search = e.target.value
    setSearch(search)

    if (e.target.value !== '') {
      fetchArtist()
    }
  }

  return (
    <div id="analyze" className="page-body">
      <span className="search-span">
        <input type="search"
               className="search-box"
               id="artist-search"
               src={"search_icon"}
               placeholder="Search Artist"
               autoComplete="off"
               spellCheck="false"
               onChange={handleChange}
        />
      </span>
      <img src={logo} className="App-logo" alt="logo" />
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
      <Artist currentResult={currentResult} />
    </div>
  );
};


