import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import spotify_black_background from './spotify-black-background.png';
//import spotify_logo from './spotify-logo.jpg';
import './App.css';

// function BannerBody(props) {
//   return (
//     <div className="banner-body">hi</div>
//   )
// }
//
// function BannerContainer(props) {
//   return (
//     <div className="banner-container"></div>
//   );
// }

class Banner extends React.Component {

  // renderBannerBody() {
  //   return <BannerBody/>
  // }

  // renderBannerContainer() {
  //   return <BannerContainer/>
  // };

  render() {
   return (
     <header className="banner">
       <div className="banner-container">
         <div className="banner-body">
           <a className="banner-title">
             <img src={spotify_black_background} className="spotify-logo" />
             <span className="title">
               Welcome!
             </span>
           </a>
           <nav aria-current="page" className="banner-nav">
             <a className="nav-tab" href="http://localhost:3000/time">
               Home
               <span className="current"/>
             </a>
             <a aria-current="page" className="nav-tab">
               Analyze
               {/*<span className="current"/>*/}
             </a>
             <a aria-current="page" className="nav-tab">
               Compare
               {/*<span className="current"/>*/}
             </a>
             <a aria-current="page" className="nav-tab">
               To Do
               {/*<span className="current"/>*/}
             </a>
           </nav>
         </div>
       </div>
     </header>
   );
  };

};

function App() {
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);

  return (
    <div className="App">
      <div className="App-header">
        <Banner/>
        <div className="page-body">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <p>The current time is {currentTime}.</p>
        </div>
      </div>
    </div>
  );
}

export default App;
